@use 'mixins';

:host {
  .circle {
    &__container {
      position: relative;
      display: flex;
      justify-content: center;
      align-items: center;
    }
    &__wrapper {
      position: relative;
    }

    &__inner {
      width: 100%;
      height: 100%;
      position: absolute;
      top: 0;
      left: 0;
      border-radius: 50%;
    }

    &__content {
      width: 100%;
      height: 100%;
      position: relative;
      display: flex;
      align-items: center;
      justify-content: center;
    }

    &__track {
      &-bar {
        @include mixins.transition;

        width: 100%;
        height: 100%;
        position: absolute;
        top: 0;
        left: 0;
        border-radius: 50%;
        background: var(--circle--track-bar-bg-color);

        &--inner {
          width: 100%;
          height: 100%;
          position: absolute;
          top: 0;
          left: 0;
          border-radius: 50%;

          .mask-inner-wrapper {
            width: 100%;
            height: 100%;
            position: absolute;
            top: 0;
            left: 0;
            display: flex;
            align-items: center;
            justify-content: center;
          }
        }

        &--bg {
          @include mixins.transition;

          width: 100%;
          height: 100%;
          position: absolute;
          top: 0;
          left: 0;
          border-radius: 50%;

          // Can be overridden
          background: var(--circle--track-bar-color);
        }
      }
    }
  }
}

